import React from 'react';
import { useParams, Link } from 'react-router-dom';
import products from '../data/productsData';

export default function ProductDetail() {
  console.log('ProductDetail import:', products);

  const { productId } = useParams();
  const product = products.find((p) => p.id === productId);

  if (!product) {
    return (
      <main className="p-8">
        <p>Product not found.</p>
        <Link to="/products" className="text-brandOrange underline">
          ← Back to Products
        </Link>
      </main>
    );
  }

  return (
    <main className="container mx-auto my-8 bg-white rounded-lg shadow overflow-hidden">
      <img
        src={product.img}
        alt={product.name}
        className="w-full h-64 object-cover rounded-t-lg"
      />
      <div className="p-6">
        <h1 className="text-3xl font-bold mb-4 text-brandPurple">{product.name}</h1>
        <p className="text-gray-700 whitespace-pre-line mb-8">{product.description}</p>
        <Link
          to="/products"
          className="inline-block px-4 py-2 bg-brandGreen text-white rounded hover:bg-brandOrange transition"
        >
          ← Back to Products
        </Link>
      </div>
    </main>
  );
}
