import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';
import banner from '../assets/banner.png';

export default function Header() {
  return (
    <header className="bg-brandPurple90">
   <div className="mx-auto px-4 max-w-[theme('spacing.container')] flex items-center h-20">
     {/* 왼쪽: 로고만 */}
     <div className="flex-shrink-0">
       <Link to="/">
         <img src={logo} alt="QLove Logo" className="h-10" />
       </Link>
     </div>
     {/* 중앙: 배너 */}
     <div className="flex-grow flex justify-center">
       <div className="relative overflow-hidden rounded-lg shadow-lg">
         <img
           src={banner}
           alt="Banner"
           className="h-12 object-contain"
         />
         {/* optional: 배너 위에 그라데이션 오버레이 */}
         <div className="absolute inset-0 bg-gradient-to-r from-transparent via-black/20 to-transparent pointer-events-none" />
       </div>
     </div>
     {/* 오른쪽: 네비게이션 */}
     <nav className="flex-shrink-0 flex gap-8 text-white">
       <Link to="/about" className="hover:opacity-80">About Us</Link>
       <Link to="/products" className="hover:opacity-80">Products</Link>
     </nav>
   </div>
 </header>

  );
}
