// src/pages/Home.js
import React from 'react';
import banner from '../assets/banner.png';
import ServiceCard from '../components/ServiceCard';

export default function Home() {
  return (
    <main className="bg-bodyBg">
      <div className="container mx-auto px-4 py-12">
        {/* 배너 섹션 */}
        <div className="flex justify-center mb-12">
          <div className="relative">
            <img
              src={banner}
              alt="Main Banner"
              className="w-full max-w-3xl rounded-lg shadow-lg"
            />
            {/* 오버레이 그라데이션 */}
            <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-transparent via-black/20 to-transparent pointer-events-none" />
          </div>
        </div>

        {/* 서비스 카드 */}
      <section className="grid gap-6 sm:grid-cols-3">
        <ServiceCard
          icon={Brush}
          title="천연 미네랄 염색"
          description="암모니아·파라벤 0 %, 특허받은 미네랄 포뮬러로 건강한 컬러를 선사합니다."
 />
        <ServiceCard
          icon={Activity}
          title="두피·탈모 케어"
   description="과학적 두피 분석과 맞춤 솔루션으로 모근부터 탄탄하게!"
 />
        <ServiceCard
            icon={Leaf}
            title="제왕충초 웰니스"
            description="프리미엄 동충하초로 일상 속 활력과 균형을 더해보세요."
 />
        </section>
      </div>
    </main>
  );
}
